import React from 'react';
import ReactDOM from 'react-dom';
import './index.scss';
import StatBlock from './Statblock.js';
import xmlData from './MMBestiary.xml';
import Autocomplete from "./Autocomplete.js";

class Main extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      dom: [],
      monsterList: [],
      isLoaded: false,
    };
  }

  // Read XML data of monsters,
  componentDidMount() {
    fetch(xmlData)
    .then(response => response.text())
    .then(res => structureData(res))
    .then(data => {
      this.setState({
        dom: data.dom,
        monsterList: data.nameList,
        search: "Ancient Red Dragon",
        isLoaded: true,
      })}
    );

    fetch('https://raw.githubusercontent.com/TheGiddyLimit/TheGiddyLimit.github.io/master/data/bestiary/bestiary-mm.json')
      .then(
      function(response) {
        if (response.status !== 200) {
          console.log('Looks like there was a problem. Status Code: ' +
            response.status);
          return;
        }

        // Examine the text in the response
        response.json().then(function(data) {
          console.log(data);
        });
      })
      .catch(function(err) {
      console.log('Fetch Error :-S', err);
    });

  }

  render() {
    if (!this.state.isLoaded) {
      return (<div>Loading...</div>);
    } else {
      return (
        <div>
          <Search monsterList={this.state.monsterList} />
          <StatBlock monster={getMonster(this.state.dom, this.state.monsterList, this.state.search)}/>
        </div>
      );
    }
  }
}

class Search extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      value: '',
      monsterList: this.props.monsterList,
    };

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(event) {
    this.setState({value: event.target.value});
  }

  handleSubmit(event) {
    alert('A name was submitted: ' + this.state.value);
    event.preventDefault();
  }

  render() {
    return (
      <div className="Search">
        <form autoComplete="off" onSubmit={this.handleSubmit}>
          <div className="autocomplete">
            <input id="myInput" type="text" placeholder="Monster" value={this.state.value} onChange={this.handleChange} />
            <Autocomplete suggestions={Object.keys(this.state.monsterList)} />
          </div>
          <input type="submit" value="Submit" />
        </form>
      </div>
    );
  }
}


// Create JS friendly dom, and include a list of all names of monsters
function structureData(d) {
  d = (new window.DOMParser()).parseFromString(d, "text/xml");
  d = Array.from(d.getElementsByTagName("monster"));
  var monsterNames = {};
  // Search for given monster
  for (var i = 0; i < d.length; i++) {
    monsterNames[d[i].getElementsByTagName("name")[0].childNodes[0].nodeValue] = i;
  }
  return {dom: d, nameList: monsterNames};
}

// Given the DOM, nameList (map) and a name, get all data of the monster (in XML DOM format)
function getMonster(d, nl, m) {
  return d[nl[m]];
}

// Creates an array of the nameList monsters
function createMonsterList(d) {
  return Object.keys(d);
}


// ========================================

ReactDOM.render(
  <Main />,
  document.getElementById('root')
);
