// Create type div
function Type(props) {
  const monster = props.monster;
  var size;
  switch (monster.size) {
    case "T":
      size = "Tiny";
      break;
    case "S":
      size = "Small";
      break;
    case "M":
      size = "Medium";
      break;
    case "L":
      size = "Large";
      break;
    case "H":
      size = "Huge";
      break;
    case "G":
      size = "Gargantuan";
      break;
    default:
      size = "UNDEFINED ("+monster.size+")";
  }

  var type;
  if (typeof monster.type === 'object') {
    type = monster.type.type + " (" + monster.type.tags[0] + ")";
  } else {
    type = monster.type;
  }

  var alignment = "";
  monster.alignment.forEach(e => {
    var partial;
    switch (e) {
      case "E":
        partial = "evil ";
        break;
      case "N":
        partial = "neutral ";
        break;
      case "G":
        partial = "good ";
        break;
      case "L":
        partial = "lawful ";
        break;
      case "C":
        partial = "chaotic ";
        break;
      default:
        partial = "UNDEFINED ("+e+") ";
    }
    alignment += partial;
  });
  alignment = alignment.slice(0,-1); // Remove last space from string

  return <div className="description">{size} {type}, {alignment}</div>;
}

