import React from 'react';

class StatBlock extends React.Component {
  render() {
    if (this.props.monster) {
      var monster = this.props.monster;
      return (
        <div className="StatBlock">
          <div className="name">{monster.name}</div>
          <Type monster={monster} />

          <TriangleHR />

          <div className="red">
              <Armor ac={monster.ac} />
              <Health hp={monster.hp} />
              <Speed speed={monster.speed} />
          </div>

          <TriangleHR />

          <table>
              <thead><tr><th>STR</th><th>DEX</th><th>CON</th><th>INT</th><th>WIS</th><th>CHA</th></tr></thead>
              <tbody><tr><td>{AbilityModifier(monster.str)}</td><td>{AbilityModifier(monster.dex)}</td><td>{AbilityModifier(monster.con)}</td><td>{AbilityModifier(monster.int)}</td><td>{AbilityModifier(monster.wis)}</td><td>{AbilityModifier(monster.cha)}</td></tr></tbody>
          </table>

          <TriangleHR />


        </div>
      );
    } else {
      return (
        <div className="StatBlock">
          Enter a monster
        </div>
      );
    }
  }
}

function Health(props) {

  return <div><span className="bold">Hit Points </span><span>{props.hp.average} ({props.hp.formula})</span></div>;
}

function Speed(props) {
  var speed = "";
  for (const [type, value] of Object.entries(props.speed)) {
    if (typeof value === "Object") {
      value = value.number + value.condition;
    }
    switch (type) {
      case "walk":
        speed += value + "ft., ";
        break;
      case "swim":
        speed += "swim " + value + "ft., ";
        break;
      case "fly":
        speed += "fly " + value + "ft., ";
        break;
      default:
        speed += "undefined speed (" + type + "), ";
    }
    speed = speed.slice(0, -2);
  }
  return <div><span className="bold">Speed </span><span>{speed}</span></div>;
}

function Armor(props) {
  var ac;
  if (props.ac[0].ac) {
    var from = "";
    props.ac[0].from.forEach(e => {
      from += bracketText(e) + ", ";
    });
    from = from.slice(0, -2);
    ac = props.ac[0].ac + " (" + from + ")";
  }
  return <div ><span className="bold">Armor </span><span>{ac}</span></div>;
}

// Create type div
function Type(props) {
  const monster = props.monster;
  var size;
  switch (monster.size) {
    case "T":
      size = "Tiny";
      break;
    case "S":
      size = "Small";
      break;
    case "M":
      size = "Medium";
      break;
    case "L":
      size = "Large";
      break;
    case "H":
      size = "Huge";
      break;
    case "G":
      size = "Gargantuan";
      break;
    default:
      size = "UNDEFINED ("+monster.size+")";
  }

  var type;
  if (typeof monster.type === 'Object') {
    type = monster.type.type + " (" + monster.type.tags + ")";
  } else {
    type = monster.type;
  }

  var alignment = "";
  monster.alignment.forEach(e => {
    var partial;
    switch (e) {
      case "E":
        partial = "evil ";
        break;
      case "N":
        partial = "neutral ";
        break;
      case "G":
        partial = "good ";
        break;
      case "L":
        partial = "lawful ";
        break;
      case "C":
        partial = "chaotic ";
        break;
      default:
        partial = "UNDEFINED ("+e+") ";
    }
    alignment += partial;
  });
  alignment = alignment.slice(0,-1); // Remove last space from string

  return <div className="description">{size} {type}, {alignment}</div>;
}

// Shorthand for triangle div
function TriangleHR(props) {
  return <div className="triangleContainer"><div className="triangle"></div></div>;
}

// Container for all actions/traits/attacks, including header. Use prefix as prefix in header
function ActionContainer(props) {
  if (props.value.length === 0) return null;
  var action = "";
  if (typeof props.prefix != 'undefined') action = props.prefix + " ";
  return (
    <div>
      <div className="actions red">{action}Actions</div>
      <div className="hr"></div>
      <Actions action={props.value} />
    </div>
  );
}

// Loop trough all actions/traits/attacks and render divs accordingly. (Without header)
function Actions(props) {
  const listActions = props.action.map((t, index) =>
    <div className="action" key={index}><span className="actionname">{t.name} </span><span>{t.text}</span></div>
  );
  return <div>{listActions}</div>;
}

// Show all different infos of the monster
function MonsterInfo(props) {
  return (
    <div className="red">
      <MonsterInfoSub type="Saving Throws" value={props.monster.save} />
      <MonsterInfoSub type="Skills" value={props.monster.skill} />
      <MonsterInfoSub type="Damage Immunities" value={props.monster.immune} />
      <MonsterInfoSub type="Senses" value={props.monster.senses} />
      <MonsterInfoSub type="Languages" value={props.monster.languages} />
      <MonsterInfoSub type="Challenge" value={props.monster.cr} />
    </div>
  );
}

// individual info, show nothing if not specified in the data
function MonsterInfoSub(props) {
  if (!props.value) return null;
  return <div><span className="bold">{props.type} </span><span>{props.value}</span></div>;
}

// Style specified text with brackets
function bracketText(s) {
  var elem;
  if (s[0] === "{" && s[s.length-1] === "}") {
    s = s.replace(/[{}']+/g,'');
    s = s.split('|')[0];
    const type = s.match(/@(\w*)/, ''); // ["@item ", "item"]
    console.log(type[1]); // "item"
    switch (type[1]) {
      case "item":
        elem = s.replace(/@(\w*)+/g,'');
        break;
      default:

    }
  } else {
    elem = s;
  }
  return elem;
}

// Returns the given (signed) modifier for an ability as string
function AbilityModifier(n) {
    var x = (n - 10) / 2; // x = (15 - 10) / 2 = 2.5 || x = (7 - 10) / 2 = -1.5
    if (x > 0) { //
      x = Math.floor(x); // x = floor(2.5) = 2
    } else {
      x = Math.ceil(x); // x = ceil(-1.5) = -1
    }

    if (x > 0) {
      x = "+" + x;
    } else if (x < 0) {
      x = "-" + x;
    } else {
      x = "" + x;
    }

    return n + " (" + x + ")";
}

export default StatBlock;
