import React from 'react';
import ReactDOM from 'react-dom';
import './index.scss';
import StatBlock from './Statblock.js';
import xmlData from './MMBestiary.xml';

class Main extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      monster: {},
      search: this.props.name,
      isLoaded: false,
    };
  }

  // Read XML data of monsters, search for given monster and update state with found data
  componentDidMount() {
    fetch(xmlData)
    .then(response => response.text())
    .then(res => structureData(res))
    .then(data => {
      this.setState({
        dom: data.dom,
        monsterList: data.nameList,
        isLoaded: true,
      })}
    );
  }


  render() {
    if (!this.state.isLoaded) {
      return (<div>Loading...</div>);
    } else {
      return (
        <div>
          <Search />
          <StatBlock name="Ancient Red Dragon"/>
        </div>
      );
    }
  }
}

function Search(props) {
  return <div className="Search">test</div>;
}

// ========================================

ReactDOM.render(
  <Main />,
  document.getElementById('root')
);

// Search the given monster m in the xml-data d.
function structureData(d) {
  d = (new window.DOMParser()).parseFromString(d, "text/xml");
  d = Array.from(d.getElementsByTagName("monster"));
  var monsterNames = [];
  // Search for given monster
  for (var i = 0; d.length; i++) {
    monsterNames[d[i].getElementsByTagName("name")[0].childNodes[0].nodeValue] = i;
  }

  return {dom: d, nameList: monsterNames};
}
