import React from 'react';
import ReactDOM from 'react-dom';
import xmlData from './MMBestiary.xml';
import XMLParser from 'react-xml-parser';

class StatBlock extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      monster: {},
      search: this.props.name,
      isLoaded: false,
    };
  }

  // Read XML data of monsters, search for given monster and update state with found data
  componentDidMount() {
    fetch(xmlData)
    .then(response => response.text())
    .then(res => structureData(res, this.state.search))
//    .then(d => console.log(d))
    .then(data => {
      this.setState({
        monster: data,
        isLoaded: true,
      })}
    );
  }

  render() {
    if (!this.state.isLoaded) {
      return (<div>Loading...</div>);
    } else {
      var {monster, search, isLoaded} = this.state;
      var size = "Medium";
      switch (monster.size) {
        case "T":
          size = "Tiny";
          break;
        case "S":
          size = "Small";
          break;
        case "M":
          size = "Medium";
          break;
        case "L":
          size = "Large";
          break;
        case "H":
          size = "Huge";
          break;
        case "G":
          size = "Gargantuan";
          break;
        default:
          size = "UNDEFINED ("+monster.size+")";
      }
      return (
        <div className="StatBlock">
          <div className="name">{monster.name}</div>
          <div className="description">{size} {monster.type}, {monster.alignment}</div>

          <div className="triangleContainer"><div className="triangle"></div></div>

          <div className="red">
              <div ><span className="bold">Armor </span><span>{monster.ac}</span></div>
              <div><span className="bold">Hit Points </span><span>{monster.hp}</span></div>
              <div><span className="bold">Speed </span><span>{monster.speed}</span></div>
          </div>

          <div className="triangleContainer"><div className="triangle"></div></div>

          <table>
              <thead><tr><th>STR</th><th>DEX</th><th>CON</th><th>INT</th><th>WIS</th><th>CHA</th></tr></thead>
              <tbody><tr><td>{AbilityModifier(monster.str)}</td><td>{AbilityModifier(monster.dex)}</td><td>{AbilityModifier(monster.con)}</td><td>{AbilityModifier(monster.int)}</td><td>{AbilityModifier(monster.wis)}</td><td>{AbilityModifier(monster.cha)}</td></tr></tbody>
          </table>

          <div className="triangleContainer"><div className="triangle"></div></div>

          <MonsterInfo />

          <div className="triangleContainer"><div className="triangle"></div></div>

          <div className="attack"><span className="attackname">Greatclub. </span><span>+6 to hit, reach 5 ft., one target. 13 (2d8+4) bludgeoning damage.</span></div>

          <div className="actions red">Actions</div>
          <div className="hr"></div>

          <div className="attack"><span className="attackname">Greatclub.</span><span className="description"> Melee Weapon Attack: </span><span>+6 to hit, reach 5 ft., one target.</span><span className="description">Hit: </span><span>13 (2d8+4) bludgeoning damage.</span></div>
          <div className="attack"><span className="attackname">Javelin.</span><span className="description"> Melee or Ranged Weapon Attack: </span><span>+6 to hit, reach 5 ft. or 30ft./120, one target.</span><span className="description">Hit: </span><span>11 (2d6+4) piercing damage.</span></div>
        </div>
      );
    }
  }
}

class MonsterInfo extends React.Component {
  render() {
    return (
      <div className="red">
        <div><span className="bold">Senses </span><span> darkvision 60ft., passive Perception 8</span></div>
        <div><span className="bold">Languages </span><span> Common, Giant</span></div>
        <div><span className="bold">Challenge </span><span> 2 (450 XP)</span></div>
      </div>
    );
  }
}

// Search the given monster m in the xml-data d.
function structureData(d, m) {
  d = (new window.DOMParser()).parseFromString(d, "text/xml");
  d = Array.from(d.getElementsByTagName("monster"));
  var monsterDOM = [];
  // Search for given monster
  for (var i = 0; d.length; i++) {
    if(d[i].getElementsByTagName("name")[0].childNodes[0].nodeValue == m) {
      monsterDOM = d[i];
      break;
    }
  }
  return XML2Monster(monsterDOM);
}

// convert XML data into JavaScript object of a monster
function XML2Monster(monsterDOM) {
  var monster = {};

  // Add nested elements (arrays) to the monster
  monster["trait"] = [];
  monster["action"] = [];
  monster["legendary"] = [];

  const elements = monsterDOM.querySelectorAll("monster > *"); // Select only immediate children
	elements.forEach(e => {
    if (e.childNodes.length == 1) {
      monster[e.tagName] = e.childNodes[0].nodeValue;
    } else {
      // Nested elements, loop through children of children of monster
      var tempElem = {};
      const elements2 = e.querySelectorAll("*");
      elements2.forEach(e2 => {
        tempElem[e2.tagName] = e2.childNodes[0].nodeValue;
      });
      monster[e.tagName].push(tempElem);

    }
	});

  console.log(monster);
  return monster;
}

// Returns the given (signed) modifier for an ability as string
function AbilityModifier(n) {
    var x = (n - 10) / 2; // x = (15 - 10) / 2 = 2.5 || x = (7 - 10) / 2 = -1.5
    if (x > 0) { //
      x = Math.floor(x); // x = floor(2.5) = 2
    } else {
      x = Math.ceil(x); // x = ceil(-1.5) = -1
    }

    if (x > 0) {
      x = "+" + x;
    } else if (x < 0) {
      x = "-" + x;
    } else {
      x = "" + x;
    }

    return n + " (" + x + ")";
}

export default StatBlock;
