  if (!props.value) return null;
  var v = "";
  props.value.forEach(e => {
    if (typeof e != 'object') {
      v += e + ", ";
    }
  });
  v = v.slice(0, -2) + "; ";
  props.value.forEach(e => {
    if (typeof e === 'object') {
      e.resist.forEach(a => {
        v += a + ", ";
      });
      if (e.note) {
        v = v.slice(0, -2) + " " + e.note + "; ";
      } else {
        v = v.slice(0, -2) + "; ";
      }
    }
  });
  v = v.slice(0, -2);
  return <MonsterInfoDiv type={props.type} value={v} />;
}
