import React from 'react';
import ReactDOM from 'react-dom';
import './index.scss';
import StatBlock from './Statblock.js';
import Autocomplete from "./Autocomplete.js";
const Bestiary = 'https://raw.githubusercontent.com/TheGiddyLimit/TheGiddyLimit.github.io/master/data/bestiary/bestiary-mm.json';

class Main extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      dom: [],
      monsterList: [],
      isLoaded: false,
    };
  }

  // Read XML data of monsters,
  componentDidMount() {
    fetch(Bestiary)
      .then(
      function(response) {
        if (response.status !== 200) {
          console.log('Looks like there was a problem. Status Code: ' +
            response.status);
          return;
        }

        // Examine the text in the response
        response.json().then(function(data) {
          console.log(data);
          data = addID(data);
          this.setState({
            data: data,
            search: "Ancient Red Dragon",
            isLoaded: true,
          });
        });
      })
      .catch(function(err) {
      console.log('Fetch Error :-S', err);
    });

  }

  render() {
    if (!this.state.isLoaded) {
      return (<div>Loading...</div>);
    } else {
      return (
        <div>
          <Search monsterList={createMonsterList(this.state.data)} />
          <StatBlock monster={getMonsterByName(this.state.data, this.state.search)}/>
        </div>
      );
    }
  }
}

class Search extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      value: '',
      monsterList: this.props.monsterList,
    };

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(event) {
    this.setState({value: event.target.value});
  }

  handleSubmit(event) {
    alert('A name was submitted: ' + this.state.value);
    event.preventDefault();
  }

  render() {
    return (
      <div className="Search">
        <form autoComplete="off" onSubmit={this.handleSubmit}>
          <div className="autocomplete">
            <input id="myInput" type="text" placeholder="Monster" value={this.state.value} onChange={this.handleChange} />
            <Autocomplete suggestions={Object.keys(this.state.monsterList)} />
          </div>
          <input type="submit" value="Submit" />
        </form>
      </div>
    );
  }
}


// Add an index to all monsters
function addID(d) {
  var monsterNames = {};
  // Search for given monster
  for (var i = 0; i < d.length; i++) {
    d[i].id = i;
  }
  return d;
}

// Given the data and a name, get all data of the monster
function getMonsterByName(d, m) {
  var monster;
  for (var i = 0; i < d.length; i++) {
    if (d[i].name === m) {
      monster = d[i];
    }
  }
  return monster;
}

// Given the data and an id, get all data of the monster
function getMonsterById(d, id) {
  return d[id];
}

// Creates an array of the nameList monsters
function createMonsterList(d) {

  return Object.keys(d);
}


// ========================================

ReactDOM.render(
  <Main />,
  document.getElementById('root')
);
